mapper.select:
        SELECT
            *
        FROM
           t_user u 
            
            
                AND u.address like CONCAT('%',#{param.Info},'%')
               or u.name like CONCAT('%',#{param.Info},'%')
             
            
              and u.uid = #{param.uid}
            
              
              and u.sex = #{param.sex}
            
             
              and u.id_card = #{param.idCard}
            
             
              and u.dept_id = #{param.deptId}
            
             
              and u.create_name = #{param.createName}
            
         
  	          and DATE_FORMAT(u.create_time, '%Y-%m-%d %T:%i:%s') >= DATE_FORMAT(#{param.startTime}, '%Y-%m-%d %T:%i:%s')   
      	
      	
  	       and DATE_FORMAT(u.create_time, '%Y-%m-%d %T:%i:%s') <= DATE_FORMAT(#{param.endTime}, '%Y-%m-%d %T:%i:%s')
